import { Link, useStaticQuery, graphql } from "gatsby"
import PropTypes from "prop-types"
import React, { useState } from "react"
import { View, Dimensions } from "react-native"
import Image from "./img"
import { useSpring, animated } from 'react-spring'

const { width, height } = Dimensions.get("window")
const Header = ({ siteTitle }) => {
  const [state, setState] = useState(false)


  return (
    <div >
      <div style={{ display: "flex", flexDirection: "row", zIndex: 1 }}>
        <Link to="/">
          <Image
            imgData={useStaticQuery(graphql`
        query {
            placeholderImage: file(relativePath: { eq: "icon/icon.png" }) {
                childImageSharp {
                    fluid(maxWidth: 300) {
                        ...GatsbyImageSharpFluid
                    }
                }
            }
        }`)}
            style={{ zIndex: 100 }}
          />
        </Link>
        <div style={{ backgroundColor: "#ff0", flex: 1, height: 100 }}>
          {/* Sec: PC Tab Division */}
          {width > 480 && <div></div>}
          {/* Sec: Mobile Division */}
          <MobileHeaderNavigation toggle={() => {
            setState(!state)
          }} />
        </div>
      </div>
      <MobileAbsoluteHeaderContainer state={state} />
    </div>
  )
}

interface MobileAbsoluteHeaderContainerProps {
  state: boolean
}
const MobileAbsoluteHeaderContainer = ({ state }: MobileAbsoluteHeaderContainerProps) => {
  const animationStyle = useSpring({ top: state ? 100 : -height })
  if (width <= 480)
    return (
      <animated.div style={{ backgroundColor: "#eee", height: height, width: "100vw", position: "absolute", top: 100, left: 0, zIndex: 1, ...animationStyle }}>

      </animated.div>
    )
  return (<></>)


}

interface MobileHeaderNavigationProps {
  toggle: () => void
}
const MobileHeaderNavigation = ({ toggle }: MobileHeaderNavigationProps) => {
  if (width <= 480)
    return (<div style={{ display: "flex", justifyContent: "flex-end", alignItems: "center", backgroundColor: "#fff", flex: 99, height: "100%" }}>
      <div style={{ height: 50, width: 50, backgroundColor: "#00f", marginRight: 15 }} onClick={() => {
        console.log("testClick")
        toggle()
      }}>
      </div>
    </div>
    )
  return (<></>)
}

Header.propTypes = {
  siteTitle: PropTypes.string,
}

Header.defaultProps = {
  siteTitle: ``,
}

export default Header
