import React, { useEffect, useLayoutEffect, useRef, useState } from 'react'
import { Container, Row, Col } from 'react-bootstrap';
import styles from "./index.module.scss";



interface Props { }
export default (props: Props) => {
    const thumbnailRef = useRef()
    const sliderRef = useRef()
    const [width, setWidth] = useState(0)
    const [left, setLeft] = useState(0)

    const slideImage = (direction: "next" | "pre") => {

        const getCurrentSlideTopOffset = (slide: number) => {
            let top = 0
            if (slide <= 0)
                return top
            else {
                top = (slide * 60)
            }
            return top
        }

        switch (direction) {
            case "next":
                if (thumbnailRef.current) {
                    console.log("thubnail Container height" + thumbnailRef.current.offsetHeight)
                    console.log("box top is <<>> " + getCurrentSlideTopOffset(left + 1))
                }
                setLeft(left + 1)
                break

            case "pre":
                console.log("move to previous image")
                if (left > 0) {
                    if (thumbnailRef.current) {
                        console.log("thubnail Container height" + thumbnailRef.current.offsetHeight)
                        console.log("box top is <<>> " + getCurrentSlideTopOffset(left + 1))
                    }
                    setLeft(left - 1)
                }
                break

            default:
                break;
        }
    }



    return (
        <Container fluid >
            <Row>
                {/* product Image slider and thumbnails */}
                <Col sm >
                    <Row className={styles.ImageSliderContainer + " "}>
                        <Col sm={2} className={styles.thumbnailsCol + " -bg-f0f- p-0"} >
                            <div className={"-bg-ff0- d-flex -jus-c- " + styles.thumbnailContainer}>
                                <div ref={thumbnailRef} className={"-bg-0ff- d-flex flex-md-column position-relative "}>
                                    {["", "", "", "", "", "", "", ""].map(() => {
                                        return (
                                            <div className="my-md-2 -mx-10-" style={{ minHeight: 50, minWidth: 50, backgroundColor: "blue" }}></div>
                                        )
                                    })}
                                </div>
                                <div className={styles.thumbnailArrow + " " + styles.thumbnailDown + " " + styles.thumbnailRight} onClick={() => { slideImage("next") }}></div>
                                <div className={styles.thumbnailArrow + " " + styles.thumbnailUp + " " + styles.thumbnailLeft} onClick={() => { slideImage("pre") }}></div>
                            </div>
                        </Col>
                        <Col sm={10} style={{ overflow: "hidden" }}>
                            <div
                                ref={sliderRef}
                                style={{
                                    height: "100%",
                                    width: "100%",
                                    backgroundColor: "red",
                                    position: "absolute",
                                    top: 0,
                                    left: `-${left * 100}%`,
                                    transition: "left 1s",
                                }}
                            >
                                <h1>{width}</h1>
                            </div>
                        </Col>
                    </Row>
                </Col>
                {/* Product infod accordion */}
                <Col sm style={{ backgroundColor: "green" }}>
                    Image Slider
                </Col>
            </Row>
        </Container >
    )
}
