import { Link, useStaticQuery, graphql } from "gatsby"
import PropTypes from "prop-types"
import React, { useState } from "react"
import { View, Dimensions } from "react-native"
import Image from "./img"
import { useSpring, animated } from 'react-spring'

const { width, height } = Dimensions.get("screen")
const Header = ({ siteTitle }) => {
  const [state, setState] = useState(false)
  const animationStyle = useSpring({ top: state ? 100 : -height })


  return (
    <div style={{ zIndex: 100 }}>
      <div style={{ display: "flex", flexDirection: "row", zIndex: 1 }}>
        <Link to="/">
          <Image
            imgData={useStaticQuery(graphql`
        query {
            placeholderImage: file(relativePath: { eq: "icon/icon.png" }) {
                childImageSharp {
                    fluid(maxWidth: 300) {
                        ...GatsbyImageSharpFluid
                    }
                }
            }
        }`)}
            style={{ backgroundColor: "#fff" }}
          />
        </Link>
        <div style={{ backgroundColor: "#ff0", flex: 1, height: 100 }}>
          {/* Sec: PC Tab Division */}
          {width > 480 && <div></div>}
          {/* Sec: Mobile Division */}
          {width <= 480 && <div style={{ display: "flex", justifyContent: "flex-end", backgroundColor: "red" }}>
            <div style={{ height: 50, width: 50, backgroundColor: "#00f" }} onClick={() => {
              console.log("testClick")
              setState(!state)
            }}>
            </div>
          </div>}
        </div>
      </div>
      {width <= 480 && <animated.div style={{ backgroundColor: "#f0f", height: height, width: "100vw", position: "absolute", top: 100, left: 0, zIndex: -1, ...animationStyle }}>

      </animated.div>}
    </div>
  )
}

Header.propTypes = {
  siteTitle: PropTypes.string,
}

Header.defaultProps = {
  siteTitle: ``,
}

export default Header
