import React, { useEffect, useLayoutEffect, useRef, useState } from 'react'
import { Container, Row, Col } from 'react-bootstrap';
import styles from "./index.module.scss";



interface Props { }
export default (props: Props) => {
    const ref = useRef(null)
    const [width, setWidth] = useState(0)
    const [left, setLeft] = useState(0)


    useEffect(() => {
        console.log('width', ref.current ? ref.current.offsetWidth : 0);
        const handleResize = () => {
            setWidth(ref.current.offsetWidth)
        }

        if (ref.current) {
            setWidth(ref.current.offsetWidth)
        }

        window.addEventListener("resize", handleResize)

        return () => {
            window.removeEventListener("resize", handleResize)
        }
    }, [ref.current])


    return (
        <Container fluid >
            <Row>
                {/* product Image slider and thumbnails */}
                <Col sm >
                    <Row className={styles.ImageSliderContainer + " "}>
                        <Col sm={3} className={styles.thumbnailsCol + " -bg-f0f-"} >
                            <div className="-bg-ff0- d-flex -jus-c- ">
                                <div className={"-bg-0ff- d-flex flex-md-column position-relative " + styles.thumbnailContainer}>
                                    {["", "", "", "", ""].map(() => {
                                        return (
                                            <div className="my-md-2 -mx-10-" style={{ minHeight: 50, width: 50, backgroundColor: "blue" }}></div>
                                        )
                                    })}
                                    <div className={styles.thumbnailArrow + " " + styles.thumbnailUp + " " + styles.thumbnailRight}></div>
                                    <div className={styles.thumbnailArrow + " " + styles.thumbnailDown + " " + styles.thumbnailLeft}></div>
                                </div>
                            </div>
                        </Col>
                        <Col sm={9}>
                            <div
                                ref={ref}
                                style={{
                                    height: "100%",
                                    width: "100%",
                                    backgroundColor: "red",
                                    position: "absolute",
                                    top: 0,
                                    transition: "left 1.5s",
                                    left
                                }}
                                onClick={() => {
                                    setLeft(left + 20)
                                }}
                            >
                                <h1>{width}</h1>
                            </div>
                        </Col>
                    </Row>
                </Col>
                {/* Product infod accordion */}
                <Col sm style={{ backgroundColor: "green" }}>
                    Image Slider
                </Col>
            </Row>
        </Container >
    )
}
