import React from 'react'
import { graphql, useStaticQuery } from 'gatsby';
import { Container, Image } from 'react-bootstrap';
import styles from "./index.module.scss"
import Img from "gatsby-image"



interface Props { }
const section = (props: Props) => {
  const data = useStaticQuery(graphql`
  query {
    svg: file(relativePath: { eq: "homepage/Path.svg" }) {
      childImageSharp {
        fluid(maxWidth: 1000, quality: 100) {
          ...GatsbyImageSharpFluid
        }
      }
      extension
      publicURL
    }
      mobileImage: file(relativePath: { eq: "homepage/section1/main-mobile.png" }) {
        childImageSharp {
          fluid(maxWidth: 1000, quality: 100) {
            ...GatsbyImageSharpFluid
          }
        }
      }
      desktopImage: file(
        relativePath: { eq: "homepage/section1/main.png" }
      ) {
        childImageSharp {
          fluid(maxWidth: 2000, quality: 100) {
            ...GatsbyImageSharpFluid
          }
        }
      }
    }       
  `)
  const sources = [
    data.mobileImage.childImageSharp.fluid,
    {
      ...data.desktopImage.childImageSharp.fluid,
      media: `(min-width: 768px)`,
    },
  ]
  return (
    <div className={styles.mainContainer} id="testDivID">
      <Img fluid={sources} />
      <SVG_1_1 data={data} />
    </div >
  )
}

const SVG_1_1 = ({ data }) => {
  const pathStyles = [styles.path_red, styles.path_yellow, styles.path_green, styles.path_cyan, styles.path_blue]
  const onHighlighterClick = ({ id }) => {
    document.getElementById("homeSec1_Overlay").classList.add(styles.overlayContainer_clear)
    switch (id) {
      case 1:
        document.getElementById("LightPath1").classList.add(styles.path1_red)
        break;

      default:
        break;
    }
  }
  return (
    <div
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
      }} >
      {/* Sec: Main OverLay */}
      <div
        id="homeSec1_Overlay"
        style={{ zIndex: 9 }}
        className={styles.overlayContainer}
        onMouseEnter={() => {
          document.getElementById("homeSec1_Overlay").classList.add(styles.overlayContainer_clear)
        }}
        onMouseLeave={() => {
          //document.getElementById("homeSec1_Overlay").classList.remove(styles.overlayContainer_clear)
        }}>
        <div className={styles.overlay} />
      </div>
      {/* Sec: Button Indigator */}
      <div className={styles.dot + " " + styles.dotAnimation}
        onClick={() => {
        }}>
        <div className={styles.dot_dot}></div>
      </div>
      {/* Sec: Light highlighters */}
      <div style={{}}>
        <div
          style={{
            position: "absolute",
            top: "6.9vw",
            left: "5.9vw",
            width: "6.6vw",
            height: "4vw",
            backgroundColor: "#fff",
            zIndex: 8,
            filter: "blur(10px)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: "6.9vw",
            right: "20vw",
            width: "6.6vw",
            height: "4vw",
            backgroundColor: "#fff",
            zIndex: 8,
            filter: "blur(10px)",
          }} />
        <div
          style={{
            position: "absolute",
            top: "6.5vw",
            right: "48.8vw",
            width: "13.4vw",
            height: "8.9vw",
            zIndex: 8,
            border: "4px solid white",
            filter: "blur(4px)",
          }} />
      </div>
      {/* Sec: SVG CONTAINER */}
      <div
        style={{
          position: "absolute",
          zIndex: 8,
          width: "100%",
          height: "100%",
          mixBlendMode: "soft-light",
          filter: "blur(40px)",
        }}>
        <svg style={{ width: "100%", height: "100%" }}
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 100% 100%">
          <ellipse id="LightPath1" cx="10vw" cy="20vw" rx="20vw" ry="30vw" />
          <ellipse id="LightPath2" cx="42.5vw" cy="15vw" rx="15vw" ry="15vw" />
          <ellipse id="LightPath3" cx="85vw" cy="25vw" rx="30vw" ry="30vw" />
        </svg>
      </div >
    </div>
  )
};

export default section;
